\n	\n	</div>\n  \n  </div>\n\n</div>\n</div>\n\n<#include \"/WEB-INF/theme/default/shop/footer.ftl\">\n<script type=\"text/javascript\" src=\"${basepath}ui/default/js/jshop.js\"></script>\n</body>\n</html>','default_myordersdetail','1');
